package com.honey.basic;

public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number1 = 230;
		int number2 = 44;
		int result = number1 + number2;
		System.out.println("the sum of two number : = " + result);

		result = number1 - number2;
		System.out.println("the difference of two number is := " + result);

		result = number1 * number2;
		System.out.println("the product of two number is := " + result);

		result = number1 / number2;
		System.out.println("the division of two number is := " + result);

		int number3 = 45;
		int number4 = 15;
		int addtionalresult = number3 + number4;

		System.out.println("the addtional result=:" + addtionalresult);

		int number5 = 60;
		int number6 = 40;
		int substractionresult = number6 - number5;
		System.out.println("the substraction result=" + substractionresult);

		int number7 = 25;
		int number8 = 99;
		int multiplicationresult = number7 * number8;
		System.out.println("the multiplication result= " +multiplicationresult);

		int number9 = 44;
		int number10 = 11;
		int divisionresult = number9 / number10;
		System.out.println("the division result=" + divisionresult);

	}
}
